mapper.select:
    select
       CART_ID cartId,USER_ID userId,CREATE_DATE createDate,UPDATE_DATE updateDate,DEL_FLG delFlg 
    from
       BRN_CART 
	where
	    DEL_FLG = 0
	     AND CART_ID = #{cartId} 
	     AND USER_ID = #{userId} 
	     AND CREATE_DATE = #{createDate} 
	     AND UPDATE_DATE = #{updateDate}
