mapper.select:
    select
       ADDRESS_ID addressId,USER_ID userId,USER_NAME userName,DEFAULT_FLG defaultFlg,ZIP zip,COUNTRY country,CITY city,ADDRESS address,PHONE phone,CELL_PHONE cellPhone,EMAIL email,CREATE_DATE createDate,UPDATE_DATE updateDate,DEL_FLG delFlg 
    from
       BRN_USER_DETAIL
	where
	    DEL_FLG = 0
	     AND ADDRESS_ID = #{addressId} 
	     AND USER_ID = #{userId} 
	     AND USER_NAME = #{userName} 
	     AND CREATE_DATE = #{createDate} 
	     AND UPDATE_DATE = #{updateDate}
